(function(){tinymce.PluginManager.requireLangPack('dbf_form');tinymce.create('tinymce.plugins.dbf_form_plugin',{init:function(ed,url){ed.addCommand('mceDbfForm',function(){ed.windowManager.open({file:url+'/dialog.php',width:360+parseInt(ed.getLang('dbf_form.delta_width',0)),height:420+parseInt(ed.getLang('dbf_form.delta_height',0)),inline:1},{plugin_url:url,some_custom_arg:'custom arg'})});ed.addButton('dbf_form',{title:'dbf_form.desc',cmd:'mceDbfForm',image:url+'/img/dbf_form.gif'});ed.onNodeChange.add(function(ed,cm,n){cm.setActive('dbf_form',n.nodeName=='IMG')})},createControl:function(n,cm){return null},getInfo:function(){return{longname:'DBF Form Plugin',author:'Matt Platts',authorurl:'http://www.mattplatts.com',infourl:'http://wiki.moxiecode.com/index.php/TinyMCE:Plugins/dbf_form',version:"1.0"}}});tinymce.PluginManager.add('dbf_form',tinymce.plugins.dbf_form_plugin)})();
