(function(){tinymce.PluginManager.requireLangPack('dbf_recordset');tinymce.create('tinymce.plugins.dbf_recordset_plugin',{init:function(ed,url){ed.addCommand('mceDbfRecordset',function(){ed.windowManager.open({file:url+'/dialog.php',width:360+parseInt(ed.getLang('dbf_recordset.delta_width',0)),height:420+parseInt(ed.getLang('dbf_recordset.delta_height',0)),inline:1},{plugin_url:url,some_custom_arg:'custom arg'})});ed.addButton('dbf_recordset',{title:'dbf_recordset.desc',cmd:'mceDbfRecordset',image:url+'/img/dbf_recordset.gif'});ed.onNodeChange.add(function(ed,cm,n){cm.setActive('dbf_recordset',n.nodeName=='IMG')})},createControl:function(n,cm){return null},getInfo:function(){return{longname:'DBF Recordset Plugin',author:'Matt Platts',authorurl:'http://www.mattplatts.com',infourl:'http://wiki.moxiecode.com/index.php/TinyMCE:Plugins/dbf_recordset',version:"1.0"}}});tinymce.PluginManager.add('dbf_recordset',tinymce.plugins.dbf_recordset_plugin)})();
